import { Button } from "../components/Button";
import '.././styles/room.scss'
import logoImg from '../images/logo.svg'
import { RoomCode } from "../components/RoomCode";
import { useParams } from 'react-router-dom'
import { database } from "../services/firebase";
import { AuthContext } from '../contexts/AuthContext'
import { useContext/* , useEffect */, useState } from 'react'
import { Question } from "../components/Question";
import { useRoom } from "../hooks/useRoom";

export function Room() {
    const { user } = useContext(AuthContext)
    const params = useParams()
    const roomId = params.id
    /*const [questions, setQuestions] = useState([])
    const [title, setTitle] = useState('')


     useEffect(() => {
        const roomRef = database.ref(`rooms/${roomId}`);
        roomRef.on('value', room => {
            const databaseRoom = room.val()
            const firebaseQuestions = databaseRoom.questions ?? {}
    const parsedQuestion = Object.entries(firebaseQuestions).map(([key, value]) => {
    return {
        id: key,
        content: value.content,
        author: value.author,
        isHighlighted: value.isHighlighted,
        isAnswered: value.isAnswered
    }})

    setTitle(databaseRoom.title)
    setQuestions(parsedQuestion)

        })
    },[roomId]) */

    const [newQuestion, setNewQuenstion] = useState('')
    const { title, questions } = useRoom(roomId)

    async function handleSendQuestion(event) {
        event.preventDefault()
        if (newQuestion.trim() === '') {
            return;
        }
        if (!user) {
            throw new Error('voce nao fez login');
        }

        const question = {
            content: newQuestion,
            author: {
                name: user.name,
                avatar: user.avatar
            },
            isHighlighted: false,
            isAnswered: false
        };

        await database.ref(`rooms/${roomId}/questions`).push(question);
        setNewQuenstion('')
    }

    async function handleLikeQuestion(questionId,likeId){
        if(likeId){
            await database.ref(`rooms/${roomId}/questions/${questionId}/likes/${likeId}`).remove()
    } else {
        await database.ref(`rooms/${roomId}/questions/${questionId}/likes`).push({
            authorId: user?.id,

        })
    }
    }
    return (
        <div id='page-room'>

            <header>

                <div className='content'>
                    <img src={logoImg} alt='letmeask' />
                    <RoomCode code={params.id}></RoomCode>
                </div>
            </header>
            <main >
                <div className='room-title' >
                    <h1>Sala {title}</h1>
                    {questions.length > 0 && <span> {questions.length} pergunta(s)</span>}
                </div>
                <form onSubmit={handleSendQuestion} >
                    <textarea
                        placeholder='O que voce quer perguntar?'
                        onChange={event => setNewQuenstion(event.target.value)}
                        value={newQuestion}
                    />
                    <div className='form-footer'>
                        {user ? (
                            <div className='user-info'>
                                <img src={user.avatar} alt={user.name} />
                                <span>{user.name}</span>
                            </div>
                        ) : (
                            <span>
                                Para enviar uma pergunta, <button>fa√ßa seu login</button>
                            </span>
                        )}
                        <Button type='submit' disabled={!user} >Enviar pergunta</Button>
                    </div>
                </form>
                <div className='question-list'>
                    {questions.map(question => {
                        return (
                            <Question key={question.id} 
                            content={question.content} 
                            author={question.author} 
                            isAnswered={question.isAnswered}
                            isHighlighted={question.isHighlighted} 
                            >

                                <button
                                    className={`like-button ${question.likeId ? 'liked' : ''}`}
                                    type='button'
                                    aria-label='Marcar favorito'
                                    onClick={()=>handleLikeQuestion(question.id,question.likeId)}
                                >
                                {question.likeCount > 0 && <span>{question.likeCount}</span>}
                                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                                        <path d="M7 22H4C3.46957 22 2.96086 21.7893 2.58579 21.4142C2.21071 21.0391 2 20.5304 2 20V13C2 12.4696 2.21071 11.9609 2.58579 11.5858C2.96086 11.2107 3.46957 11 4 11H7M14 9V5C14 4.20435 13.6839 3.44129 13.1213 2.87868C12.5587 2.31607 11.7956 2 11 2L7 11V22H18.28C18.7623 22.0055 19.2304 21.8364 19.5979 21.524C19.9654 21.2116 20.2077 20.7769 20.28 20.3L21.66 11.3C21.7035 11.0134 21.6842 10.7207 21.6033 10.4423C21.5225 10.1638 21.3821 9.90629 21.1919 9.68751C21.0016 9.46873 20.7661 9.29393 20.5016 9.17522C20.2371 9.0565 19.9499 8.99672 19.66 9H14Z" stroke="#737380" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
                                    </svg>

                                </button>
                            </Question>
                        )
                    })}
                </div>
            </main>


        </div>
    )
}
